package com.example.esoftwaricaclone

import android.os.Bundle
import com.google.android.material.bottomnavigation.BottomNavigationView
import androidx.appcompat.app.AppCompatActivity
import androidx.navigation.findNavController
import androidx.navigation.ui.AppBarConfiguration
import androidx.navigation.ui.setupActionBarWithNavController
import androidx.navigation.ui.setupWithNavController
import com.example.esoftwaricaclone.model.Student

class MainActivity : AppCompatActivity() {
    companion object {
       var studentArrayList  = ArrayList<Student>()
    }
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        val navView: BottomNavigationView = findViewById(R.id.nav_view)

        val navController = findNavController(R.id.nav_host_fragment)
        val appBarConfiguration = AppBarConfiguration(setOf(
                R.id.navigation_home, R.id.navigation_dashboard, R.id.navigation_notifications))
        setupActionBarWithNavController(navController, appBarConfiguration)
        navView.setupWithNavController(navController)
        loadData()
    }
    private fun loadData(){
        studentArrayList.add(Student(1,"Rajan","Male",21,"Baluwatar","data:image/jpeg;base64,"))
        studentArrayList.add(Student(2,"Pushpa","Female",21,"Thankot","data:image/jpeg;base64,"))
        studentArrayList.add(Student(3,"Rajina","Others",21,"Dhapasi","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTBQYX3ICsqUMRfJ8UYDytEHHHlQ2YG6XRz2Q&usqp=CAU"))
    }

}