package com.example.esoftwaricaclone.ui.home

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.lifecycle.Observer
import androidx.lifecycle.ViewModelProvider
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.esoftwaricaclone.R
import com.example.esoftwaricaclone.adapter.PostAdapter
import com.example.esoftwaricaclone.model.Student

class HomeFragment : Fragment() {
    private lateinit var postrecycler : RecyclerView
    private var studentList  = ArrayList<Student>()
    override fun onCreateView(
            inflater: LayoutInflater,
            container: ViewGroup?,
            savedInstanceState: Bundle?
    ): View? {
        val view = inflater.inflate(R.layout.fragment_home, container, false)
        postrecycler = view.findViewById(R.id.postrecycle)

        loadStudent()
        studentList.add(Student(1,"Rajan","Male",21,"Baluwatar","data:image/jpeg;base64,"))
        studentList.add(Student(2,"Pushpa","Female",21,"Thankot","data:image/jpeg;base64,"))
        studentList.add(Student(3,"Rajina","Others",21,"Dhapasi","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTBQYX3ICsqUMRfJ8UYDytEHHHlQ2YG6XRz2Q&usqp=CAU"))
        val adapterView = context?.let { PostAdapter(it,studentList) }
        postrecycler.layoutManager= LinearLayoutManager(context)
        postrecycler.adapter = adapterView
        return view
    }
    private fun loadStudent(){
        val usid = arguments?.getInt("sid")
        val usname = arguments?.getString("sname")
        val usage = arguments?.getInt("sage")
        val usgender = arguments?.getString("sgender")
        val usaddress = arguments?.getString("saddress")
        val usimage = arguments?.getString("simage")
        if (usid != null) {
            studentList.add(Student(usid, usname, usgender, usage, usaddress, usimage))
        }
    }
}